  const [currentCategory, setCurrentCategory] = useState(sections[0]);

  return (
    <div>
      <Nav
        sections={sections}
        setCurrentCategory={setCurrentCategory}
        currentCategory={currentCategory}
      ></Nav>
      <section>
        <About></About>
